import { useState } from "react";
import { Bell, User } from "lucide-react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";

export default function Header() {
  return (
    <header className="flex justify-between items-center p-4 bg-gray-900 text-white">
      <h1 className="text-xl font-bold">Dashboard</h1>
      <div className="flex items-center gap-4">
        <DropdownNotifications />
        <DropdownProfile />
      </div>
    </header>
  );
}

function DropdownNotifications() {
  const [open, setOpen] = useState(false);
  const notifications = [
    "New comment on your post",
    "Server maintenance scheduled",
    "Update available",
  ];

  return (
    <div className="relative">
      <Button onClick={() => setOpen(!open)} variant="ghost">
        <Bell className="w-5 h-5" />
      </Button>
      {open && (
        <Card className="absolute right-0 mt-2 w-64 bg-white text-black shadow-lg">
          <CardContent className="p-2">
            {notifications.length > 0 ? (
              notifications.map((notif, index) => (
                <div key={index} className="p-2 border-b last:border-none">
                  {notif}
                </div>
              ))
            ) : (
              <p className="p-2 text-center">No new notifications</p>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

function DropdownProfile() {
  const [open, setOpen] = useState(false);

  return (
    <div className="relative">
      <Button onClick={() => setOpen(!open)} variant="ghost">
        <User className="w-5 h-5" />
      </Button>
      {open && (
        <Card className="absolute right-0 mt-2 w-48 bg-white text-black shadow-lg">
          <CardContent className="p-2">
            <p className="p-2 border-b">Profile</p>
            <p className="p-2 border-b">Settings</p>
            <p className="p-2 text-red-500 cursor-pointer">Logout</p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}
