import React, { useState, useRef, useEffect } from "react";
import { createPortal } from "react-dom";

const Dropdown = ({ isOpen, onClose }) => {
  if (!isOpen) return null;

  return createPortal(
    <ul
      className="absolute right-0 mt-2 w-48 bg-white border rounded-lg shadow-lg z-[9999]"
    >
      <li className="p-2 hover:bg-gray-100 cursor-pointer">Profile</li>
      <li className="p-2 hover:bg-gray-100 cursor-pointer">Settings</li>
      <li className="p-2 hover:bg-gray-100 cursor-pointer">Logout</li>
    </ul>,
    document.body
  );
};

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef(null);

  // Close dropdown if clicked outside
  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div className="relative bg-gray-200 p-4 flex justify-end">
      <button
        ref={dropdownRef}
        className="bg-gray-300 px-4 py-2 rounded-lg flex items-center gap-2"
        onClick={() => setIsOpen(!isOpen)}
      >
        <span>John Doe</span>
        <span>â–¼</span>
      </button>
      <Dropdown isOpen={isOpen} onClose={() => setIsOpen(false)} />
    </div>
  );
};

export default Navbar;
